import { useLocation } from 'react-router-dom';
import styled from 'styled-components';
import Home from './components/Home';

import './css/normalize.css';
import './css/responsive.css';

const Main = styled.main`
  display: grid;
  grid-template-columns: 1fr;
  grid-template-rows: 1fr;
  background-color: #000;
`;
const Bg = styled.img`
  grid-column: 1/2;
  grid-row: 1/2;
  object-fit: cover;
  max-height: 100vh;
  max-width: 100vw;
  transition: all 0.5s ease;
  min-height: ${props=>props.path === '/' ? '110%' : '100%'};
  min-width: ${props=>props.path === '/' ? '110%' : '100%'};
  filter: blur(${props=>props.path === '/' ? '1px' : '2px'});
`;
const ShadowA = styled.div`
  grid-column: 1/2;
  grid-row: 1/2;
  display: grid;
  grid-template-columns: 1fr;
  grid-template-rows: 1fr;
  background-color: #0009;
  z-index: 1000;
`;
const ShadowB = styled.div`
  grid-column: 1/2;
  grid-row: 1/2;
  display: ${props=>props.path === '/' ? 'none' : 'flex'};
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 100%;
  background-color: #0003;
  z-index: 2000;
`;

function App() {
  const pathName = useLocation().pathname;
  return (
    <Main>
      <Bg src='./img/bg.jpg' path={pathName}/>
      <ShadowA>
        <Home/>
        <ShadowB path={pathName}>
          x
        </ShadowB>
      </ShadowA>
    </Main>
  );
};

export default App;
