import styled from 'styled-components';

const Wrapper = styled.div`
    grid-column: 1/2;
    grid-row: 1/2;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    background-color: #0008;
    z-index: 1000;
`;
const Circle = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    border: .25rem solid #fff;
    border-radius: 50%;
    width: 10rem;
    height: 10rem;
    padding: 1rem;
`;
const Logo = styled.img`
    object-fit: cover;
`;
const Line = styled.div`
    width: .25rem;
    height: 4rem;
    background-color: #fff;
`;
const Info = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    align-items: center;
    border-top: .25rem solid #fff;
    border-bottom: .25rem solid #fff;
    width: 80vw;
    height: 20rem;
    padding: 2rem 0;
`;
const Name = styled.h2`
    text-align: center;
    font-size: 3rem;
    font-weight: 600;
    letter-spacing: .75rem;
    color: #fff;
`;
const Job = styled.h1`
    text-align: center;
    font-size: 1.5rem;
    font-weight: 500;
    letter-spacing: .5rem;
    color: #ccc;
`;
const Nav = styled.nav`
    display: flex;
    flex-direction: column;
    width: 50vw;
    border: .25rem solid #fff;
    border-radius: .5rem;
    @media screen and (min-width: 500px) {
        flex-direction: row;
        width: 70vw;
    };
`;
const Container = styled.div`
    flex-grow: 1;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 2rem;
    font-weight: 300;
    letter-spacing: .4rem;
    color: #fff;
    height: 5rem;
    border-top: ${props=>props.left ? '.25rem solid #fff' : 'none'};
    border-bottom: ${props=>props.right ? '.25rem solid #fff' : 'none'};
    cursor: pointer;
    transition: all 0.5s ease;
    &:hover{
        background-color: #fff2;
    };
    @media screen and (min-width: 500px) {
        border-top: none;
        border-bottom: none;
        border-right: ${props=>props.right ? '.25rem solid #fff' : 'none'};
        border-left: ${props=>props.left ? '.25rem solid #fff' : 'none'};
        height: 6.5rem;
    };
`;

const Home = () => {
    return (
        <Wrapper>
            <Circle>
                <Logo src='./img/logo2.png'/>
            </Circle>
            <Line/>
            <Info>
                <Name>BRYAN FLORES</Name>
                <Job>Frontend Developer</Job>
            </Info>
            <Line/>
            <Nav>
                <Container right>Portafolio</Container>
                <Container>Sobre m√≠</Container>
                <Container left>Contacto</Container>
            </Nav>
        </Wrapper>
    );
};

export default Home;
